package edu.icet.bo.asset.impl;import com.fasterxml.jackson.databind.ObjectMapper;import edu.icet.bo.asset.OrderBo;import edu.icet.dao.DaoFactory;import edu.icet.dao.crud.impl.OrderDaoImpl;import edu.icet.entity.OrderEntity;import edu.icet.util.DaoType;import jakarta.persistence.criteria.Order;public class OrderBoImpl implements OrderBo {    private final OrderDaoImpl orderDaoImpl = DaoFactory.getInstance().getDao(DaoType.ORDER);    @Override    public void saveOrder(Order order){        new OrderDaoImpl().insert(                new ObjectMapper().convertValue(order, OrderEntity.class));    }    @Override    public String getLatestOrderId(){        return orderDaoImpl.getLatestOrderId();    }    public Order getOrderById(String orderId){        return new ObjectMapper()                .convertValue(orderDaoImpl.search(orderId), Order.class);    }    @Override    public boolean deleteOrderById(String id){        return orderDaoImpl.delete(id);    }}