package edu.icet.bo.asset.impl;import com.fasterxml.jackson.databind.ObjectMapper;import edu.icet.bo.asset.PlaceOrderBo;import edu.icet.dao.DaoFactory;import edu.icet.dao.crud.impl.PlaceOrderDaoImpl;import edu.icet.dao.crud.impl.ProductDaoImpl;import edu.icet.entity.OrderEntity;import edu.icet.entity.ProductEntity;import edu.icet.model.Order;import edu.icet.model.Product;import edu.icet.util.DaoType;import javafx.collections.FXCollections;import javafx.collections.ObservableList;public class PlaceOrderBoImpl implements PlaceOrderBo {    private final PlaceOrderDaoImpl placeOrderDao;    public PlaceOrderBoImpl() {        this.placeOrderDao = DaoFactory.getInstance().getDao(DaoType.ORDER);    }    @Override    public String generateOrderId(){        String id = new PlaceOrderDaoImpl().getLatestOrderId();        if (id == null){            return "B0001";        }        int number = Integer.parseInt(id.split("B")[1]);        number++;        return String.format("B%04d", number);    }    @Override    public ObservableList<OrderEntity> getAllOrders(){        return placeOrderDao.getAll();    }    @Override    public void saveOrder(Order order){        new PlaceOrderDaoImpl().insert(                new ObjectMapper().convertValue(order, OrderEntity.class));    }    @Override    public String getLatestOrderId(){        return placeOrderDao.getLatestOrderId();    }    @Override    public Order getOrderById(String orderId){        return new ObjectMapper()                .convertValue(placeOrderDao.search(orderId), Order.class);    }    @Override    public void saveOrder(org.hibernate.query.Order order) {    }    @Override    public boolean deleteOrderById(String id){        return placeOrderDao.delete(id);    }}